mapper.select:
select a.ID, a.CYZG_CARD, a.NAME, a.SEX, a.BORNDATE, a.IDCARD, a.ADDRESS, a.PHONE, a.DRIVE_CARD, a.DRIVE_START_DATE, a.CARID, a.CARTYPE, a.COMPANY, a.ENGAGE_CONN, a.ENGAGE_TIME, a.CONTRACT_STRCOUNT, a.CONTRACT_ENDCOUNT, a.PASSTIME, a.STATUS, a.ADDTIME, b.PERSONAL_ID from tb_employee_continue_register a , tb_employee b where 1 = 1 and a.IDCARD = b.IDCARD
and ( a.CYZG_CARD like CONCAT(CONCAT('%', #{pd.title}),'%') or a.NAME like CONCAT(CONCAT('%', #{pd.title}),'%') or a.CARID like CONCAT(CONCAT('%', #{pd.title}),'%') or a.PHONE like CONCAT(CONCAT('%', #{pd.title}),'%') or a.ADDRESS like CONCAT(CONCAT('%', #{pd.title}),'%') )
and a.COMPANY = #{pd.company}
and a.STATUS = #{pd.status}
order by a.ADDTIME desc
