mapper.select:
select a.ID, a.CYZG_CARD, a.NAME, a.SEX, a.BORNDATE, a.IDCARD, a.ADDRESS, a.PHONE, a.DRIVE_CARD, a.DRIVE_START_DATE, a.CARID, a.CARTYPE, a.COMPANY, a.ENGAGE_CONN, a.ENGAGE_TIME, a.CONTRACT_STRCOUNT, a.CONTRACT_ENDCOUNT, a.PASSTIME, a.STATUS, a.ADDTIME, b.PERSONAL_ID as personalId from tb_employee_register a , tb_employee b where 1 = 1 and a.IDCARD = b.IDCARD
AND a.NAME like '%'+ #{name}+'%'
AND a.CYZG_CARD like '%'+ #{cyzgCard}+'%'
AND a.STATUS = #{status}
AND a.COMPANY = #{company}
