mapper.select:
select
from tb_employee_replace_sign
AND NAME like '%'+ #{name}+'%'
AND OLD_SIGN_ID like '%'+ #{cyzgCard}+'%'
AND STATUS = #{status}
AND COMPANY = #{company}

mapper.update:
		update tb_employee_replace_sign set STATUS = #{status,jdbcType=VARCHAR},
		 PASSTIME = #{passtime,jdbcType=VARCHAR}
		where ID in
  			 
                  #{item}
